import React, { useState } from "react";

import "./UserInput.css";

const UserInput = () => {
  const [currentSavings, setCurrentSavings] = useState("");
  const [yearlyContribution, setYearlyContribution] = useState("");
  const [expectedInterest, setExpectedInterest] = useState("");
  const [investmentDuration, setInvestmentDuration] = useState("");

  const currentSavingHanlder = (event) => {
    setCurrentSavings(event.target.value);
  };

  const yearlySavingHandler = (event) => {
    setYearlyContribution(event.target.value);
  };

  const expectedInterestHandler = (event) => {
    setExpectedInterest(event.target.value);
  };

  const investmentDurationHandler = (event) => {
    setInvestmentDuration(event.target.value);
  };

  const submitHandler = (event) => {
    event.preventDefault();

    // const userInputData = {
    //   currentSaving: currentSavings,
    //   yearlySaving: yearlyContribution,
    //   interest: expectedInterest,
    //   investment: investmentDuration,
    // };

    // console.log(userInputData);
    // setCurrentSavings("");
    // setYearlyContribution("");
    // setExpectedInterest("");
    // setInvestmentDuration("");
  };

  const resetHandler = () => {
    // ..
    console.log("RESET");
  };

  return (
    <form className="form" onSubmit={submitHandler}>
      <div className="input-group">
        <p>
          <label htmlFor="current-savings">Current Savings ($)</label>
          <input type="number" id="current-savings" value={currentSavings} onChange={currentSavingHanlder} />
        </p>
        <p>
          <label htmlFor="yearly-contribution">Yearly Savings ($)</label>
          <input type="number" id="yearly-contribution" value={yearlyContribution} onChange={yearlySavingHandler} />
        </p>
      </div>
      <div className="input-group">
        <p>
          <label htmlFor="expected-return">Expected Interest (%, per year)</label>
          <input type="number" id="expected-return" value={expectedInterest} onChange={expectedInterestHandler} />
        </p>
        <p>
          <label htmlFor="duration">Investment Duration (years)</label>
          <input type="number" id="duration" value={investmentDuration} onChange={investmentDurationHandler} />
        </p>
      </div>
      <p className="actions">
        <button onClick={resetHandler} type="reset" className="buttonAlt">
          Reset
        </button>
        <button type="submit" className="button">
          Calculate
        </button>
      </p>
    </form>
  );
};

export default UserInput;
