import React, { useState } from "react";

import "./UserInput.css";

const initialUserInput = {
  "current-savings": 10000,
  "yearly-contribution": 1200,
  "expected-return": 7,
  duration: 10,
};

console.log(userInputData);
setCurrentSavings("");
setYearlyContribution("");
setExpectedInterest("");
setInvestmentDuration("");

const UserInput = () => {
  const [userInput, setUserInput] = useState({
    initialUserInput,
  });

  // const currentSavingHanlder = (event) => {
  //   setCurrentSavings(event.target.value);
  // };

  // const yearlySavingHandler = (event) => {
  //   setYearlyContribution(event.target.value);
  // };

  // const expectedInterestHandler = (event) => {
  //   setExpectedInterest(event.target.value);
  // };

  // const investmentDurationHandler = (event) => {
  //   setInvestmentDuration(event.target.value);
  // };

  const inputChangeHandler = (input, value) => {
    setUserInput((prevInput) => {
      return {
        ...prevInput,
        [input]: value,
      };
    });
  };

  const submitHandler = (event) => {
    event.preventDefault();
  };

  const resetHandler = () => {
    initialUserInput();
  };

  return (
    <form className="form" onSubmit={submitHandler}>
      <div className="input-group">
        <p>
          <label htmlFor="current-savings">Current Savings ($)</label>
          <input onChange={(event) => inputChangeHandler("current-savings", event.target.value)} type="number" id="current-savings" />
        </p>
        <p>
          <label htmlFor="yearly-contribution">Yearly Savings ($)</label>
          <input onChange={(event) => inputChangeHandler("yearly-contribution", event.target.value)} type="number" id="yearly-contribution" />
        </p>
      </div>
      <div className="input-group">
        <p>
          <label htmlFor="expected-return">Expected Interest (%, per year)</label>
          <input onChange={(event) => inputChangeHandler("expected-return", event.target.value)} type="number" id="expected-return" />
        </p>
        <p>
          <label htmlFor="duration">Investment Duration (years)</label>
          <input onChange={(event) => inputChangeHandler("duration", event.target.value)} type="number" id="duration" />
        </p>
      </div>
      <p className="actions">
        <button onClick={resetHandler} type="reset" className="buttonAlt">
          Reset
        </button>
        <button type="submit" className="button">
          Calculate
        </button>
      </p>
    </form>
  );
};

export default UserInput;
